package ru.airdead.hudrenderer.stuffimport net.minecraft.client.MinecraftClientimport ru.airdead.hudrenderer.event.Eventimport ru.airdead.hudrenderer.event.EventManager.eventListenersclass ClientApi {    fun minecraft(): MinecraftClient? = MinecraftClient.getInstance()    fun <T : Event> registerEvent(eventClass: Class<T>, listener: (T) -> Unit) {        @Suppress("UNCHECKED_CAST")        eventListeners.computeIfAbsent(eventClass) { mutableListOf() }.add(listener as (Event) -> Unit)    }    inline fun <reified T : Event> onEvent(noinline listener: (T) -> Unit) {        registerEvent(T::class.java, listener)    }}